import elixirLogo from "assets/elixirs/500-1000Elixirs.png";
import { PAYMENTS_LIST, PAYMENT_TYPE } from "../../utils/constants";

import s from "./index.module.scss";
import { useState } from "react";

export const TrovoLive = ({ payments = [] }) => {
  const [selectedPayment, setSelectedPayment] = useState("");
  const [qiwiTel, setQiwiTel] = useState("");

  const isMobileNumberShow = selectedPayment === PAYMENT_TYPE.qiwi;

  return (
    <div className={s["trovo-live"]}>
      <main className={s["trovo-live__main"]}>
        <h3 className={s["title"]}>Способы оплаты</h3>
        <ul className={s["payments-list"]}>
          {PAYMENTS_LIST.map((broker) => {
            const isPaymentSelected = selectedPayment === broker.name;

            return (
              <li
                className={`${s["broker"]} ${
                  isPaymentSelected && s["broker--selected"]
                }`}
                onClick={() => setSelectedPayment(broker.name)}
              >
                <img src={broker.image} className={s["broker__image"]} />
              </li>
            );
          })}
        </ul>
        {isMobileNumberShow ? (
          <input
            className={s["form-fields__input"]}
            placeholder="Номер телефона"
            type="tel"
            value={qiwiTel}
            onChange={(e) => setQiwiTel(e.target.value)}
            required
          />
        ) : (
          <></>
        )}
        <footer className={s["trovo-live__footer"]}></footer>
      </main>
    </div>
  );
};
