import elixirLogo from "assets/elixirs/500-1000Elixirs.png";
import { PAYMENTS_LIST } from "../../utils/constants";

import s from "./index.module.scss";
import { useState } from "react";

export const TrovoLive = ({ payments = [] }) => {
  const [selectedPayment, setSelectedPayment] = useState("");

  return (
    <div className={s["trovo-live"]}>
      <main className={s["trovo-live__content"]}>
        <h3 className={s["title"]}>Способы оплаты</h3>
        <ul className={s["payments-list"]}>
          {PAYMENTS_LIST.map((broker) => {
            const isPaymentSelected = selectedPayment === broker.name;

            return (
              <li
                className={`${s["broker"]} ${
                  isPaymentSelected && s["broker--selected"]
                }`}
              >
                <img src={broker.image} className={s["broker__image"]} />
              </li>
            );
          })}
        </ul>
      </main>
    </div>
  );
};
